# straitforward linetree


class Solution:
    def createSortedArray(self, instructions) -> int:
        v_max = max(instructions) + 1
        self.lt = [0] * (4 * v_max)
        ans = 0
        mod = pow(10, 9) + 7
        for v in instructions:
            ql = self.querySum(0, v - 1, 0, v_max, 1)
            qr = self.querySum(v + 1, v_max, 0, v_max, 1)
            ans += min(ql, qr)
            ans %= mod 
            self.add(v, 0, v_max, 1)
        return ans 

    def add(self, v, left, right, idx):
        if left == right:
            self.lt[idx] += 1
            return 
        
        mid = (left + right) // 2
        if v <= mid:
            self.add(v, left, mid, 2 * idx)
        else:
            self.add(v, mid + 1, right, 2 * idx + 1)
        self.lt[idx] = self.lt[2 * idx] + self.lt[2 * idx + 1]
    
    def querySum(self, ql, qr, left, right, idx):
        if left == right:
            return self.lt[idx]
        
        mid = (left + right) // 2
        s = 0
        if ql <= mid:
            s += self.querySum(ql, qr, left, mid, 2 * idx)
        if qr > mid:
            s += self.querySum(ql, qr, mid + 1, right, 2 * idx + 1)
        return s
            
            
